import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { Grid, AppBar, Toolbar, Typography, Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';

import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';

const styleAppBar = {
    backgroundColor: '#0A4776'
}

const styleTypography = {
    fontSize:'14px'
}

const styleIcon = {
    fontSize:'15px',
    marginRight:'5px'
}

class Header extends Component {
    state = {  }
    render() { 
        return (
            <AppBar style={styleAppBar} position='static'>
                <Toolbar>
                    <Grid container>
                        <Grid item xs={10}>
                            <Typography variant='h6'>
                                Aji-shop
                            </Typography>
                        </Grid>
                        <Grid item xs={2}>
                            <Router>
                                <Route exact path='/'>
                                    <Button variant='contained' color='primary' fullWidth>
                                        <ShoppingCartIcon style={styleIcon} />
                                        <Typography style={styleTypography} variant='caption'>
                                            Cart ({this.props.itemCounter || '0'})
                                        </Typography>
                                    </Button>
                                </Route>
                                <Route exact path='/cart'>'>
                                    <Button variant='contained' color='primary' fullWidth>
                                        <MonetizationOnIcon style={styleIcon} />
                                        <Typography style={styleTypography} variant='caption'>
                                            Store
                                        </Typography>
                                    </Button>
                                </Route>
                            </Router>
                        </Grid>
                    </Grid>
                </Toolbar>
            </AppBar>
        );
    }
}
 
export default Header;
